// Leaderboard.js
import React from "react";
import "./Leaderboard.css";

const Leaderboard = ({ topUsers, users }) => {
  return (
    <div className="leaderboard-container">
      <h2 className="leaderboard-heading">
        <span role="img" aria-label="sparkle">
          🌟
        </span>{" "}
        Monthly Wealth Ranking
      </h2>

      <div className="top-three-container">
        <h3>
          <span role="img" aria-label="trophy">
            🏆
          </span>{" "}Top 3 Performers
        </h3>
        <div className="top-three-cards">
          {topUsers.map((user, index) => (
            <div key={user.name} className={`top-card place-${index + 1}`}>
              <strong>#{index + 1}</strong>
              <p>{user.name}</p>
              <p>{user.points} pts</p>
            </div>
          ))}
        </div>
      </div>

      <div className="full-leaderboard">
        <h3>
          <span role="img" aria-label="clipboard">
            📝
          </span>{" "}Full Leaderboard
        </h3>
        <table>
          <thead>
            <tr>
              <th>Rank</th>
              <th>Name</th>
              <th>Points</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user, index) => (
              <tr key={user.name}>
                <td>{index + 1}</td>
                <td>{user.name}</td>
                <td>{user.points}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Leaderboard;
