import React from 'react';
function Leaderboard({ users }) {
  return (
    <div style={{ marginTop: 30 }}>
      <h2>Leaderboard</h2>
      <ol>
        {users.map((user, index) => (
          <li key={user._id}>
            <strong>{index + 1}. {user.name}</strong> - {user.totalPoints} pts
          </li>
        ))}
      </ol>
    </div>
  );
}
export default Leaderboard;
