// frontend/src/components/Leaderboard.js
import React from 'react';
import './Leaderboard.css'; // optional for styling

const medalColors = ['#FFD700', '#C0C0C0', '#CD7F32']; // gold, silver, bronze

const Leaderboard = ({ users }) => {
  const sorted = [...users].sort((a, b) => b.points - a.points);
  const topThree = sorted.slice(0, 3);
  const rest = sorted.slice(3);

  return (
    <div style={{ marginTop: 30 }}>
      <h2>üèÜ Top 3 Earners</h2>
      <div style={{ display: 'flex', justifyContent: 'center', gap: '2rem' }}>
        {topThree.map((user, index) => (
          <div key={user._id} style={{
            background: medalColors[index],
            padding: '20px',
            borderRadius: '10px',
            textAlign: 'center',
            width: '120px',
            color: '#fff',
            fontWeight: 'bold',
            boxShadow: '0 4px 8px rgba(0,0,0,0.2)'
          }}>
            <div style={{ fontSize: '24px' }}>#{index + 1}</div>
            <div>{user.name}</div>
            <div>{user.points} pts</div>
          </div>
        ))}
      </div>

      <h2 style={{ marginTop: 40 }}>üìã Full Leaderboard</h2>
      <table style={{ width: '100%', marginTop: 20, borderCollapse: 'collapse' }}>
        <thead>
          <tr style={{ backgroundColor: '#eee' }}>
            <th style={{ padding: 10 }}>Rank</th>
            <th style={{ padding: 10 }}>Name</th>
            <th style={{ padding: 10 }}>Points</th>
          </tr>
        </thead>
        <tbody>
          {rest.map((user, idx) => (
            <tr key={user._id}>
              <td style={{ padding: 10 }}>{idx + 4}</td>
              <td style={{ padding: 10 }}>{user.name}</td>
              <td style={{ padding: 10 }}>{user.points}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Leaderboard;
