// Placeholder for: frontend/src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import UserDropdown from './components/UserDropdown';
import ClaimButton from './components/ClaimButton';
import Leaderboard from './components/Leaderboard';

function App() {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState('');

  const fetchUsers = async () => {
    const res = await axios.get('http://localhost:3000/api/users');
    setUsers(res.data);
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleClaim = async () => {
    if (!selectedUser) return;
    await axios.post('http://localhost:3000/api/claim', { userId: selectedUser });
    fetchUsers();
  };

  return (
    <div style={{ padding: 40, fontFamily: 'Arial' }}>
      <h1>Monthly Wealth Ranking ðŸŒŸ</h1>
      <UserDropdown users={users} onChange={setSelectedUser} />
      <ClaimButton onClick={handleClaim} />
      <Leaderboard users={users} />
    </div>
  );
}

export default App;
