import React, { useEffect, useState } from 'react';
import axios from 'axios';

import './App.css';

function Leaderboard({ users }) {
  return (
    <div className="leaderboard">
      {users.map((user, index) => (
        <div className="user-row" key={user._id}>
          <div className="user-info">
            <span className="rank">#{index + 1}</span>
            <span className="name">{user.name}</span>
          </div>
          <span className="points-bubble">{user.points} pts</span>
        </div>
      ))}
    </div>
  );
}

function App() {
  const [users, setUsers] = useState([]);
  const [selectedUserId, setSelectedUserId] = useState('');
  const [claimedPoints, setClaimedPoints] = useState(null);

  const fetchUsers = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/users');
      const sortedUsers = res.data.sort((a, b) => b.points - a.points);
      setUsers(sortedUsers);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const handleClaimPoints = async () => {
    if (!selectedUserId) return alert('Please select a user first.');

    try {
      const res = await axios.post(`http://localhost:5000/api/users/${selectedUserId}/claim`);
      setClaimedPoints(res.data.claimedPoints);
      fetchUsers(); // Refresh leaderboard
    } catch (error) {
      console.error('Error claiming points:', error);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  return (
    <div className="container">
      <h1 className="title">ðŸŒŸ Monthly Wealth Ranking</h1>

      <select
        className="dropdown"
        value={selectedUserId}
        onChange={(e) => setSelectedUserId(e.target.value)}
      >
        <option value="">Select a user</option>
        {users.map((user) => (
          <option key={user._id} value={user._id}>
            {user.name}
          </option>
        ))}
      </select>

      <button className="claim-button" onClick={handleClaimPoints}>
        Claim Points
      </button>

      {claimedPoints !== null && (
        <p className="claimed-points">
          ðŸŽ‰ You claimed {claimedPoints} points!
        </p>
      )}

      <Leaderboard users={users} />
    </div>
  );
}

export default App;
