import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Leaderboard from 'Leaderboar';

function App() {
  const [users, setUsers] = useState([]);
  const [selectedUserId, setSelectedUserId] = useState('');
  const [claimedPoints, setClaimedPoints] = useState(null);

  // Fetch all users
  const fetchUsers = async () => {
    try {
      const res = await axios.get('http://localhost:3000/api/users');
      setUsers(res.data);
    } catch (err) {
      console.error('Error fetching users:', err);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  // Handle claim button
  const handleClaimPoints = async () => {
    if (!selectedUserId) {
      alert('Please select a user');
      return;
    }

    try {
      const res = await axios.post('http://localhost:3000/api/claim', {
        userId: selectedUserId,
      });

      const claimed = res.data.claimedPoints;
      setClaimedPoints(claimed);
      await fetchUsers(); // Refresh leaderboard
    } catch (err) {
      console.error('Error claiming points:', err);
      alert('Failed to claim points');
    }
  };

  return (
    <div style={{ padding: '30px', fontFamily: 'Arial' }}>
      <h1>üåü Monthly Wealth Ranking</h1>

      <select
        value={selectedUserId}
        onChange={(e) => setSelectedUserId(e.target.value)}
        style={{ padding: '8px', marginRight: '10px' }}
      >
        <option value="">Select a user</option>
        {users.map((user) => (
          <option key={user._id} value={user._id}>
            {user.name}
          </option>
        ))}
      </select>

      <button onClick={handleClaimPoints} style={{ padding: '8px 16px' }}>
        Claim Points
      </button>

      {claimedPoints !== null && (
        <p style={{ color: 'green', fontWeight: 'bold', marginTop: '10px' }}>
          üéâ You claimed {claimedPoints} points!
        </p>
      )}

      <Leaderboard users={users} />
    </div>
  );
}

export default App;
